mapper.select:
select exists(
    select user_id
    from user
    where user_id = #{user_id}
)

mapper.insert:
insert into user(
                 user_id,
                 password,
                 nickname
) values(
         #{user_id},
         #{password},
         #{nickname}
                )

mapper.update:
update user
set password = #{password},
    nickname = #{nickname}
where user_id = #{user_id}

mapper.select:
select user_id, nickname, time_created
from user
where user_id=#{user_id}
