mapper.select:
select *
from user
where user_id=#{userId} and password=#{password}

select exists(
    select user_id
    from user
    where user_id = #{userId}
)

mapper.insert:
insert into user(
                 user_id,
                 password,
                 nickname
) values(
         #{userId},
         #{password},
         #{nickname}
                )

mapper.update:
update user
set password = #{newPassword},
    nickname = #{nickname}
where user_id = #{userId}

mapper.select:
select user_id, nickname, time_created
from user
where user_id=#{userId}
