mapper.select:
select uid, user_id, nickname, time_created, access_id
from user
where user_id=#{id} and password=#{pw}

mapper.insert:
insert into user(
                 user_id,
                 password,
                 nickname
) values(
         #{user_id},
         #{password},
         #{nickname}
                )

mapper.update:
update user
set password = #{password},
    nickname = #{nickname}
where user_id = #{user_id}
