mapper.select:
select uid, user_id, nickname, time_created, access_id
from user
where user_id=#{id} and password=#{pw}

select exists(
    select user_id
    from user
    where user_id = #{id}
)

mapper.insert:
insert into user(
                 user_id,
                 password,
                 nickname
) values(
         #{user_id},
         #{password},
         #{nickname}
                )
